<?phpRoute::group([    'prefix'     => 'quiz',    'namespace'  => 'Quiz',], function() {    /**     * Category Management     */    Route::group(['namespace' => 'Category'], function() {        Route::resource('categories', 'CategoryController');    });    /**     * Question Management     */    Route::group(['namespace' => 'Question'], function() {        Route::resource('questions', 'QuestionController');    });    /**     * Poll Management     */    Route::group(['namespace' => 'Poll'], function() {        Route::resource('polls', 'PollController');    });    /**     * Tasks Management     */    Route::group(['namespace' => 'Task'], function() {        Route::get('tasks/completed', 'TaskController@completed')->name('admin.quiz.tasks.completed');        Route::get('tasks/active', 'TaskController@active')->name('admin.quiz.tasks.active');        Route::resource('tasks', 'TaskController');    });});