<?phpRoute::group([    'prefix'     => 'quiz',    'namespace'  => 'Quiz',], function() {    /**     * Category Management     */    Route::group(['namespace' => 'Category'], function() {        Route::resource('categories', 'CategoryController', ['except' => ['show']]);    });    /**     * Question Management     */    Route::group(['namespace' => 'Question'], function() {        Route::resource('questions', 'QuestionController');    });    /**     * Poll Management     */    Route::group(['namespace' => 'Poll'], function() {        Route::resource('polls', 'PollController');    });    /**     * Tasks Management     */    Route::group(['namespace' => 'Task'], function() {        Route::resource('tasks', 'TaskController');    });});